import React, { useState, useEffect } from "react";
import "./App.css";
import environment from "./environments/environment";

//Components
import Header from "./components/header";
import Footer from "./components/footer";
import Card from "./components/card";

function App(props) {
  //State
  const [subscribers, setSubcribers] = useState([]);

  //Hook
  useEffect(() => {
    const subscribersUrl = environment.serverUrl;
    async function getSubscribers() {
      const response = await fetch(subscribersUrl);
      const subscribersData = await response.json();
      setSubcribers(subscribersData);
      console.log(subscribersData);
    }
    getSubscribers();
    return;
  }, []);

  return (
    <div className='App'>
      <Header />
      <div>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>lastName</th>
              <th>Age</th>
              <th>Sport</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              {subscribers.map(subscriber => {
                return (
                  <p key={subscriber.id}>
                    <td>{subscriber.name}</td>
                    <td>{subscriber.lastName}</td>
                    <td>{subscriber.age}</td>
                    <td>{subscriber.sport}</td>
                  </p>
                );
              })}
            </tr>
          </tbody>
        </table>
      </div>
      <Card />
      <Footer />
    </div>
  );
}

export default App;
