import React, { useState, useEffect } from "react";
import "./App.css";
import environment from "./environments/environment";

//Components
import Header from "./components/header";
import Footer from "./components/footer";
import Card from "./components/card";

function App(props) {
  //State
  const [subscribers, setSubcribers] = useState([]);

  //Hook
  useEffect(() => {
    const subscribersUrl = environment.serverUrl;
    async function getSubscribers() {
      const response = await fetch(subscribersUrl);
      const subscribersData = await response.json();
      setSubcribers(subscribersData);
      console.log(subscribersData);
    }
    getSubscribers();
    return;
  }, []);

  //SortByName
  const byName = function sortByName() {
    function compare(a, b) {
      const nameA = a.name.toUpperCase();
      const nameB = b.name.toUpperCase();

      let comparison = 0;
      if (nameA > nameB) {
        comparison = 1;
      } else if (nameA < nameB) {
        comparison = -1;
      }
      return comparison;
    }

    const nameSorted = subscribers.sort(compare);
    setSubcribers(nameSorted);
    console.log(nameSorted);
  };

  return (
    <div className='App'>
      <Header />
      <div>
        <table className='App-header'>
          <thead>
            <tr>
              <th>Name</th>
              <th>lastName</th>
              <th>Age</th>
              <th>Sport</th>
            </tr>
          </thead>
          <tbody>
            {subscribers.map(subscriber => {
              return (
                <tr key={subscriber.id}>
                  <td>{subscriber.name}</td>
                  <td>{subscriber.lastName}</td>
                  <td>{subscriber.age}</td>
                  <td>{subscriber.sport}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      <button onClick={byName}>Sort By Name</button>

      <button>Sort By Age</button>
      <button>Sort By Sport</button>
      <Card />
      <Footer />
    </div>
  );
}

export default App;
